import React from 'react';

interface Props {
    title?: string;
    minimize: () => void;
    headerAvatar?: string;
    headerStyle?: object;
    titleColor?: string;
    minimizeIcon?: string;
}

const defaultMinimizeIcon = 'data:image/png;base64, ';

const Header: React.FC<Props> = ({ title, minimize, headerAvatar, headerStyle, titleColor, minimizeIcon }) => {
    return (
        <header className="chat-header" style={headerStyle}>
            <div className="d-flex align-items-center">
                {headerAvatar && <div className="chat-header__avatar">
                    <img src={headerAvatar} alt="avatar" />
                </div>}
                <div className="chat-header__title" style={{ color: titleColor }}>{title ? title : 'Chat Demo'}</div>
            </div>
            <img src={minimizeIcon ? minimizeIcon : defaultMinimizeIcon} alt="minimize" className="chat-header__minimize" onClick={minimize} />
        </header>
    );
}
export default Header;

import React from 'react';
import { Message } from '../chat/Chat';

export type Position = 'left' | 'right';

interface Props {
    message: Message;
    position: Position;
    style?: object;
}

const Bubble: React.FC<Props> = ({ message, position, style }) => {
    return (
        <div className={position === 'left' ? 'chat-bubble__wrapper chat-bubble--left' : 'chat-bubble__wrapper chat-bubble--right'}>
            <div className="chat-bubble__content" style={style}>{message.text}</div>
        </div>
    );
}
export default Bubble;

import React from 'react';
import Moment from 'react-moment';
import moment from 'moment';

interface Props {
    date: string;
    locale?: string;
    style?: object;
}

const Timestamp: React.FC<Props> = ({ date, style }) => {
    return (
        <div className="chat-timestamp" style={style}>
            <Moment format="dd. o HH:mm">{moment(date)}</Moment>
        </div>
    );
}
export default Timestamp;

import React from 'react';

interface Props {
    source: string;
}

const Avatar: React.FC<Props> = ({ source }) => {
    return (
        <div className="chat-message__avatar">
            <img src={source} alt="avatar" />
        </div>
    );
}
export default Avatar;

import React from 'react';

const TypingIndicator: React.FC = () => {
    return (
        <div className="chat__typing-indicator">
            <div className="chat__typing-indicator__content">
                <div className="dot"></div>
                <div className="dot"></div>
                <div className="dot"></div>
            </div>
        </div>
    );
}
export default TypingIndicator;

import React, { useEffect, useState } from 'react';
import Bubble, { Position } from '../bubble/Bubble';
import { Message, User } from '../chat/Chat';
import moment from 'moment';
import Timestamp from '../timestamp/Timestamp';
import Avatar from '../avatar/Avatar';
import TypingIndicator from '../typing_indicator/TypingIndicator';

interface Props {
    messages: Message[];
    user: User;
    isTyping?: boolean;
    leftBubbleStyle?: object;
    rightBubbleStyle?: object;
    backgroundColor?: string;
    timestampStyle?: object;
}

const Body: React.FC<Props> = ({ messages, user, isTyping, leftBubbleStyle, rightBubbleStyle, backgroundColor, timestampStyle }) => {
    const el = React.useRef() as React.MutableRefObject<HTMLDivElement>;
    const [typingUserAvatar, setTypingUserAvatar] = useState<string | undefined>();

    useEffect(() => {
        el.current.scrollIntoView();
    }, [messages, isTyping]);

    useEffect(() => {
        setTypingUserAvatar(messages.find(message => message.user.id !== user.id)?.user.avatar);
    }, [messages, user]);

    const setStylesForBubbles = (messages: Message[], i: number, position: Position) => {
        const currentMessage = messages[i];
        const previousMessage = messages[i-1];
        const nextMessage = messages[i+1];

        let corner1 = '0px';
        let corner2 = '0px';
        let corner3 = '0px';
        let corner4 = '0px';

        let topTimestamp = previousMessage && moment(currentMessage.createdAt).diff(moment(previousMessage.createdAt), 'minutes') > 5 ? true : false;
        const bottomTimestamp = nextMessage && moment(nextMessage.createdAt).diff(moment(currentMessage.createdAt), 'minutes') > 5 ? true : false;

        if(!topTimestamp) {
            topTimestamp = !previousMessage ? true : false;
        }

        if(topTimestamp && !previousMessage) {
            corner1 = '15px';
            corner2 = '15px';
        }

        if(bottomTimestamp) {
            corner3 = '15px';
            corner4 = '15px';
        }

        if(topTimestamp) {
            corner1 = '15px';
            corner2 = '15px';
        }

        if(currentMessage.user.id !== previousMessage?.user.id) {
            corner1 = '15px';
            corner2 = '15px';
        }

        if(!nextMessage) {
            corner3 = '15px';
            corner4 = '15px';
        }

        if(currentMessage.user.id !== nextMessage?.user.id) {
            corner3 = '15px';
            corner4 = '15px';
        }

        if(currentMessage.user.id === previousMessage?.user.id) {
            corner2 = '15px';
        }

        if(currentMessage.user.id === nextMessage?.user.id) {
            corner3 = '15px';
        }

        if(position === 'left') {
            return { borderRadius: `${corner1} ${corner2} ${corner3} ${corner4}` }
        }

        return { borderRadius: `${corner2} ${corner1} ${corner4} ${corner3}` }
    }

    return (
        <div className="chat-body" style={{ background: backgroundColor }}>
            {messages.map((message, i) => {
                const previousMessage = messages[i-1];
                const nextMessage = messages[i+1];

                let position: Position = 'left';
                let style;

                /* set style curves for bubbles */
                if(message.user.id === user.id) {
                    position = 'right';

                    style = setStylesForBubbles(messages, i, 'right');
                } else {
                    style = setStylesForBubbles(messages, i, 'left');
                }

                /* check if timestamp is needed */
                let isTimeStamp = true;
                if(previousMessage) {
                    const diff = moment(message.createdAt).diff(moment(previousMessage.createdAt), 'minutes');
                    if(diff < 5) {
                        isTimeStamp = false;
                    }
                }

                /* check if render avatar */
                let isAvatarVisible = false;
                if(position === 'left' &&
                    ((
                        nextMessage && message.user.id !== nextMessage.user.id
                    ) || (
                        nextMessage &&
                        moment(nextMessage.createdAt).diff(moment(message.createdAt), 'minutes') > 5
                    ) || (
                        !nextMessage
                    ))
                    && message.user.avatar
                ) {
                    isAvatarVisible = true;
                }

                return (
                    <div key={i} style={nextMessage ? (message.user.id !== nextMessage.user.id ? { marginBottom: '8px' } : {}) : { marginBottom: '8px' }}>
                        {isTimeStamp && message.createdAt ? <Timestamp date={message.createdAt} style={timestampStyle} /> : null}

                        <div className="chat-body__message">
                            {message.user.avatar && isAvatarVisible && <Avatar source={message.user.avatar} />}
                            {message.user.avatar && !isAvatarVisible && <div style={{ marginLeft: '30px' }} />}
                            <Bubble message={message} position={position} style={{...style, ...position === 'left' ? leftBubbleStyle : rightBubbleStyle }} />
                        </div>
                    </div>
                );
            })}

            {isTyping &&
            <div className="chat-body__typing-indicator">
                {typingUserAvatar !== undefined && <Avatar source={typingUserAvatar} />}
                <TypingIndicator />
            </div>}

            <div ref={el}></div>
        </div>
    );
}
export default Body;

import React, { useState, useEffect, useRef } from 'react';
import { Message, User } from '../chat/Chat';
import moment from 'moment';

const defaultSendIcon = 'data:image/png;base64, ';

interface Props {
    onSend?: (message: Message) => void;
    user: User;
    onInputTextChanged?: (value: string) => void;
    inputToolbarStyle?: object;
    inputStyle?: object;
    sendIcon?: string;
}

const Input: React.FC<Props> = ({ user, onSend, onInputTextChanged, inputToolbarStyle, inputStyle, sendIcon }) => {
    const [value, setValue] = useState<string>('');
    const [message, setMessage] = useState<Message>();
    const el = useRef() as React.MutableRefObject<HTMLInputElement>;

    useEffect(() => {
        onInputTextChanged && onInputTextChanged(value);
        setMessage({
            text: value,
            createdAt: moment().format('YYYY-MM-DD HH:mm:ss'),
            user: user
        });
    }, [value, onInputTextChanged]);
    
    const send = () => {
        if(message && onSend) {
            onSend(message);
        }
        setValue('');
        el.current.focus();
    }

    const handleKeyDown = (e: any) => {
        if (e.key === 'Enter') {
            send();
        }
    }

    return (
        <div className="chat-input" style={inputToolbarStyle}>
            <input
                type="text"
                placeholder="Type a message..."
                value={value}
                onChange={e => setValue(e.target.value)}
                onKeyDown={handleKeyDown}
                ref={el}
                style={inputStyle}
            />
            {value.length > 0 && <img src={sendIcon ? sendIcon : defaultSendIcon} alt="send" onClick={send} />}
        </div>
    );
}
export default Input;

import React from 'react';

const defaultChatIcon = 'data:image/png;base64, ';

interface Props {
    onClick: () => void;
    icon?: string;
    style?: object;
}

const Widget: React.FC<Props> = ({ onClick, icon, style }) => {
    return (
        <img src={icon ? icon : defaultChatIcon} alt="chat" className="chat-widget" onClick={onClick} style={style} />
    );
}
export default Widget;

import React, { useState } from 'react';
import Header from '../header/Header';
import Body from '../body/Body';
import Input from '../input/Input';
import Widget from '../widget/Widget';

export interface User {
    id: number | string;
    name?: string;
    avatar?: string;
}

export interface Message {
    id?: number | string;
    text: string;
    createdAt?: string;
    user: User;
}

interface Props {
    title?: string;
    minimized?: boolean;
    messages: Message[];
    user: User;
    onSend?: (message: Message) => void;
    isTyping?: boolean;
    onInputTextChanged?: (value: string) => void;
    headerAvatar?: string;
    headerStyle?: object;
    titleColor?: string;
    minimizeIcon?: string;
    leftBubbleStyle?: object;
    rightBubbleStyle?: object;
    backgroundColor?: string;
    timestampStyle?: object;
    inputToolbarStyle?: object;
    inputStyle?: object;
    sendIcon?: string;
    chatIcon?: string;
    containerStyle?: object;
    widgetStyle?: object;
}

const Chat: React.FC<Props> = ({
    title,
    minimized,
    messages,
    user,
    onSend,
    isTyping,
    onInputTextChanged,
    headerAvatar,
    headerStyle,
    titleColor,
    minimizeIcon,
    leftBubbleStyle,
    rightBubbleStyle,
    backgroundColor,
    timestampStyle,
    inputToolbarStyle,
    inputStyle,
    sendIcon,
    chatIcon,
    containerStyle,
    widgetStyle
}) => {
    const [isMinimized, setIsMinimized] = useState(minimized);

    return (
        <>
        {isMinimized ? <Widget
            onClick={() => setIsMinimized(false)}
            icon={chatIcon}
            style={widgetStyle}
        /> :
        <div className="chat-container" style={containerStyle}>
            <Header
                title={title}
                minimize={() => setIsMinimized(true)}
                headerAvatar={headerAvatar}
                headerStyle={headerStyle}
                titleColor={titleColor}
                minimizeIcon={minimizeIcon}
            />
            <Body
                user={user}
                messages={messages}
                isTyping={isTyping}
                leftBubbleStyle={leftBubbleStyle}
                rightBubbleStyle={rightBubbleStyle}
                backgroundColor={backgroundColor}
                timestampStyle={timestampStyle}
            />
            <Input
                user={user}
                onSend={onSend}
                onInputTextChanged={onInputTextChanged}
                inputToolbarStyle={inputToolbarStyle}
                inputStyle={inputStyle}
                sendIcon={sendIcon}
            />
        </div>}
        </>
    );
}
export default Chat;
